mapper.update:
update t_ad_resource
channel_id = #{channelId}, ad_name = #{adName}, ad_title = #{adTitle}, ad_icon = #{adIcon}, ad_banner = #{adBanner}, desktop_icon = #{desktopIcon}, ad_images = #{adImages}, ad_package = #{adPackage}, file_size = #{fileSize}, version = #{version}, support_platform = #{supportPlatform}, app_type_id = #{appTypeId}, charge_type = #{chargeType}, price = #{price}, clearing_form = #{clearingForm}, downloads = #{downloads}, recommens = #{recommens}, ad_rating = #{adRating}, language = #{language}, description = #{description}, apk_url = #{apkUrl}, shelf_date = #{shelfDate}, start_time = #{startTime}, end_time = #{endTime}, status = #{status}, recom = #{recom}, is_del = #{isDel}, modify_date = now()
where id = #{id}

mapper.select:
select id, channel_id, ad_name, ad_title, ad_icon, ad_banner, desktop_icon, ad_images, ad_package, file_size, version, support_platform, app_type_id, charge_type, price, clearing_form, downloads, recommens, ad_rating, language, description, modify_date, create_date, apk_url, shelf_date, start_time, end_time, status, recom, is_del from t_ad_resource where 1=1
and ad_name like '%${param.adName}%'
and is_del=#{param.isDel}
and is_del=0
order by modify_date asc limit #{slimt},#{pageSize}
