mapper.select:
select a.idate, sum(case when a.kpi_code = 'sm_total_users' then a.kpi_value else 0 end) as totalUsers, sum(case when a.kpi_code = 'sm_push_times' then a.kpi_value else 0 end) as pushTimes, sum(case when a.kpi_code = 'sm_display_times' then a.kpi_value else 0 end) as displayTimes, sum(case when a.kpi_code = 'sm_new_users' then a.kpi_value else 0 end) as newUsers, sum(case when a.kpi_code = 'sm_online_users' then a.kpi_value else 0 end) as onlineUsers, sum(case when a.kpi_code = 'sm_conversion_rate' then a.kpi_value else 0 end) as conversionRate, sum(case when a.kpi_code = 'sm_fill_rate' then a.kpi_value else 0 end) as fillRate, sum(case when a.kpi_code = 'sm_money_pushes' then a.kpi_value else 0 end) as moneyPushes, sum(case when a.kpi_code = 'sm_money_advertising' then a.kpi_value else 0 end) as moneyAdvertising, sum(case when a.kpi_code = 'sm_app_opens' then a.kpi_value else 0 end) as appOpens from t_report_daily as a inner join t_apps as b on a.app_id = b.id where a.uid=#{uid}
and a.idate between #{_startDate} and #{_endDate}
AND idate >= #{_startDate}
AND #{_endDate} >= idate
AND app_id = #{appId}
group by a.idate,a.app_id limit #{slimt},#{pageSize}

select a.idate, b.name, sum(case when a.kpi_code = 'sm_total_users' then a.kpi_value else 0 end) as totalUsers, sum(case when a.kpi_code = 'sm_push_times' then a.kpi_value else 0 end) as pushTimes, sum(case when a.kpi_code = 'sm_display_times' then a.kpi_value else 0 end) as displayTimes, sum(case when a.kpi_code = 'sm_new_users' then a.kpi_value else 0 end) as newUsers, sum(case when a.kpi_code = 'sm_online_users' then a.kpi_value else 0 end) as onlineUsers, sum(case when a.kpi_code = 'sm_conversion_rate' then a.kpi_value else 0 end) as conversionRate, sum(case when a.kpi_code = 'sm_fill_rate' then a.kpi_value else 0 end) as fillRate, sum(case when a.kpi_code = 'sm_money_pushes' then a.kpi_value else 0 end) as moneyPushes, sum(case when a.kpi_code = 'sm_money_advertising' then a.kpi_value else 0 end) as moneyAdvertising, sum(case when a.kpi_code = 'sm_app_opens' then a.kpi_value else 0 end) as appOpens from t_report_daily as a inner join t_apps as b on a.app_id = b.id where a.uid=#{uid}
and a.idate between #{_startDate} and #{_endDate}
AND idate >= #{_startDate}
AND #{_endDate} >= idate
AND app_id = #{appId}
group by a.idate,a.app_id limit #{slimt},#{pageSize}

	select count(1) from (select 1
	from t_report_daily as a inner join t_apps as b on a.app_id = b.id 
	where a.uid=#{uid}


	      and a.idate between #{_startDate} and #{_endDate}


	      AND idate >= #{_startDate}


	      AND #{_endDate}  >=  idate



     		AND app_id = #{appId}

	 group by a.idate,a.app_id) as report

select a.idate, sum(case when a.kpi_code = 'sm_total_users' then a.kpi_value else 0 end) as totaLusers, sum(case when a.kpi_code = 'sm_push_times' then a.kpi_value else 0 end) as pushTimes, sum(case when a.kpi_code = 'sm_display_times' then a.kpi_value else 0 end) as displayTimes, sum(case when a.kpi_code = 'sm_new_users' then a.kpi_value else 0 end) as newUsers, sum(case when a.kpi_code = 'sm_online_users' then a.kpi_value else 0 end) as onlineUsers, sum(case when a.kpi_code = 'sm_conversion_rate' then a.kpi_value else 0 end) as conversionRate, sum(case when a.kpi_code = 'sm_fill_rate' then a.kpi_value else 0 end) as fillRate, sum(case when a.kpi_code = 'sm_money_pushes' then a.kpi_value else 0 end) as moneyPushes, sum(case when a.kpi_code = 'sm_money_advertising' then a.kpi_value else 0 end) as moneyAdvertising, sum(case when a.kpi_code = 'sm_app_opens' then a.kpi_value else 0 end) as appOpens from t_report_monthly as a inner join t_apps as b on a.app_id = b.id where a.uid=#{uid}
and a.idate between #{_startDate} and #{_endDate}
AND idate >= #{_startDate}
AND #{_endDate} >= idate
AND app_id = #{appId}
group by a.idate,a.app_id limit #{slimt},#{pageSize}

	select count(1) from (select 1
	from t_report_monthly as a inner join t_apps as b on a.app_id = b.id 
	where a.uid=#{uid}


	      and a.idate between #{_startDate} and #{_endDate}


	      AND idate >= #{_startDate}


	      AND #{_endDate}  >=  idate



     		AND app_id = #{appId}

	 group by a.idate,a.app_id) as report
